contracts/DrawingManagerv2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

error Promotion_Expired();
error InvalidConfig(); 
error NotApproved();

event DrawingCreated(address indexed owner, uint256 indexed promotionID, address indexed erc20Token, uint256 maxSlots, uint256 endTime, uint256 price, uint256 cohortSize, uint256 rebateAmount, bool mintsNFTs);
event DrawingCustodyRedeemed(uint256 indexed promotionID, uint256 redeemedAmount); 
event DrawingCancelled(uint256 indexed promotionID, uint256 numberOfParticipants);
event DrawingReceiptRedeemed(uint256 indexed tokenID, uint256 indexed promotionID, uint256 redeemedAmount); 
event DrawingReceiptsMinted(address indexed joiner, uint256 indexed promotionID, uint256 indexed firstParticipantNumber, uint256 firstTokenID, uint256 numTickets);
event RaffleWinner(uint256 indexed promotionID, uint256 cohort, uint256 tokenID, uint256 participantNumber, uint256 prize); 
event RaffleInitiated(uint256 indexed promotionID, uint256 indexed vrfRequestID, address indexed initiator, uint256 cohort); //initiators should be reimbursed more in potential air-drops

import "hardhat/console.sol";
import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./PromotionManagerv9.sol";

contract DrawingManager is VRFConsumerBaseV2, PromotionManager {

    struct Drawing {
        uint256 maxSlots;
        uint256 endTime;
        uint256 price;
        uint256 cohortSize;
        uint256 rebateAmount;
        address owner; 
        bool returnedCustody;
        address erc20Token;
        bool mintsNFTs;
    }

    struct DrawingReceipt {
        uint256 drawingID;
        uint256 redeemableAmount;
    }

    struct VRFRequestContext {
        uint256 drawingID;
        uint256 cohort;
        uint256 randomWord; 
    }

    uint256 public drawingIDs = 200;// type(uint256).max / 10 * 2 + 1;
    uint256 public constant MINIMUM_DURATION = 900; 


    mapping(uint256 => DrawingReceipt) public DrawingReceipts;
    mapping(uint256 => Drawing) public Drawings;
    mapping(uint256 => VRFRequestContext) vrfRequestIDtoContext; 
    mapping(uint256=> mapping(uint256 => uint256)) drawingCohortsToVRFRequestID;

    /* State Variables */
    VRFCoordinatorV2Interface private immutable i_vrfCoordinator;

    uint16 private constant REQUEST_CONFIRMATIONS = 3;
    uint32 private immutable i_callbackGasLimit;
    uint64 private immutable i_subscriptionId;
    uint256 private s_lastTimeStamp;
    bytes32 private immutable i_gasLane;

    constructor(
        address vrfCoordinatorV2,
        address _receiptManagerAddress,
        address _promotionsManagerAddress, 
        bytes32 gasLane,
        uint64 subscriptionId,
        uint32 callbackGasLimit
    ) VRFConsumerBaseV2(vrfCoordinatorV2) { 
        owner = msg.sender; // Set the owner to the contract deployer
        bank = payable(owner);
        receiptManagerAddress = _receiptManagerAddress;  
        receiptManager = IReceiptManager(_receiptManagerAddress);
        promotionsManagerAddress = _promotionsManagerAddress;  
        promotionsManager = IPromotionsManager(_promotionsManagerAddress);
        i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_gasLane = gasLane;
        i_subscriptionId = subscriptionId;
        i_callbackGasLimit = callbackGasLimit;
    }

    function createDrawing(
        uint256 _maxSlots,
        uint256 _duration,
        uint256 _price,
        uint256 _cohortSize,
        uint256 _rebateAmount,
        address _owner,
        address _erc20Token, 
        bool _mintsNFTs
    ) public onlyApprovedOperators(_owner) {
        if (_cohortSize<=1 || _maxSlots % _cohortSize != 0  || _duration < MINIMUM_DURATION || _maxSlots <= 1 || _rebateAmount >= _price*_cohortSize) {
            revert InvalidConfig();
        }

        // Initialize a new drawing contract and store it in storage
        Drawing storage drawing = Drawings[drawingIDs];
        drawing.maxSlots = _maxSlots;
        drawing.owner = payable(_owner);
        drawing.endTime = block.timestamp + _duration;
        drawing.price = _price;
        drawing.cohortSize = _cohortSize;
        drawing.rebateAmount = _rebateAmount;
        drawing.erc20Token = _erc20Token; 
        drawing.mintsNFTs = _mintsNFTs;

        emit DrawingCreated(_owner, drawingIDs, _erc20Token, _maxSlots, _duration + block.timestamp, _price, _cohortSize, _rebateAmount, _mintsNFTs); 
        addressToPromotions[_owner].push(drawingIDs);
        if (_mintsNFTs) {
            receiptManager.setPromotionOwner(drawingIDs, _owner); 
        }
        drawingIDs+=1; 
    }

    function joinDrawing(uint256 drawingID, uint256 numOrders) public {
        Drawing memory drawing = Drawings[drawingID];
        uint256 numParticipants = promotionIDToReceiptIDs[drawingID].length;
        console.log("1");
        // Check if the promotion is expired or slots are full
        if (numParticipants >= drawing.maxSlots ||  drawing.endTime < block.timestamp) {
            revert Promotion_Expired();
        } 
        // Adjust order number if exceeding max slots
        else if(numParticipants + numOrders > drawing.maxSlots) {
            numOrders = drawing.maxSlots - numParticipants;
        }
        console.log("2");
        // Calculate amounts
        uint256 totalRebate = drawing.rebateAmount * numOrders;
        uint256 totalSellerAmount = (drawing.price - drawing.rebateAmount) * numOrders;
        uint256 commissionAmount = (totalSellerAmount) * commission / 10000;
        uint256 totalAmount = totalRebate + totalSellerAmount;
        console.log(totalRebate);
        console.log(totalSellerAmount);
        console.log(commissionAmount);
        console.log(totalAmount);
        console.log("9");
        // Transfer funds
        IERC20(drawing.erc20Token).transferFrom(msg.sender, address(this), totalAmount);
        console.log("3a");
        IERC20(drawing.erc20Token).transfer(promotionsManager.getReceiverAddress(drawing.owner, drawing.erc20Token), totalSellerAmount - commissionAmount);
        console.log("4");
        // Update balances
        earnedCommissions[drawing.erc20Token] += commissionAmount;

        // Mint NFT receipts and log details
        uint256 initialReceiptID;
        if (drawing.mintsNFTs) {
            initialReceiptID = receiptManager.mintReceipts(
                msg.sender,
                drawingID,
                numParticipants + 1,
                numOrders
            );
        } else {
            initialReceiptID = receiptManager.incrementReceiptIDs(numOrders);
        }
        console.log("5");

        for (uint256 i = 0; i < numOrders; ++i) {
            DrawingReceipt storage drawingReceipt = DrawingReceipts[initialReceiptID + i];
            drawingReceipt.drawingID = drawingID; 

            // Directly push to storage array
            promotionIDToReceiptIDs[drawingID].push(initialReceiptID + i);

            // Handle unminted receipt ownership
            if (!drawing.mintsNFTs) {
                unmintedReceiptsToOwners[initialReceiptID + i] = msg.sender;
            }
        }
        console.log("6");

        // Update the number of participants
        emit DrawingReceiptsMinted(msg.sender, drawingID, numParticipants +1, initialReceiptID, numOrders);
        console.log("7");
    }

    function drawingEligibility(uint256 drawingID, uint256 cohort) public view returns (bool) {
        Drawing storage drawing = Drawings[drawingID];
        if(promotionIDToReceiptIDs[drawingID].length < drawing.cohortSize*(1+cohort) || vrfRequestIDtoContext[drawingCohortsToVRFRequestID[drawingID][cohort]].drawingID !=0) {
            return false; 
        }
        else {
            return true;
        } 
    } 

    function initiateDrawing(uint256 drawingID, uint256 cohort) public  {
        require(drawingEligibility(drawingID, cohort), "Ineligible");         
        uint256 requestId = i_vrfCoordinator.requestRandomWords(
            i_gasLane, i_subscriptionId, REQUEST_CONFIRMATIONS, i_callbackGasLimit, 1
        ); //check this, change 1 to something else
        vrfRequestIDtoContext[requestId].drawingID = drawingID;
        vrfRequestIDtoContext[requestId].cohort = cohort; 
        drawingCohortsToVRFRequestID[drawingID][cohort] = requestId; 
        emit RaffleInitiated(drawingID, requestId , msg.sender,cohort); 
    } 

    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override{
        VRFRequestContext memory context = vrfRequestIDtoContext[requestId];
        uint256 cohortSize = Drawings[context.drawingID].cohortSize; 

        //Store random word
        vrfRequestIDtoContext[requestId].randomWord = randomWords[0]; 

        //Identify the winner. Check the winning participant number is winnerIndex +1
        uint256 winnerIndex = (context.cohort*cohortSize)  + (randomWords[0]%cohortSize);
        uint256 winningReceiptID = promotionIDToReceiptIDs[context.drawingID][winnerIndex]; 
        uint256 winningAmount = Drawings[context.drawingID].rebateAmount*cohortSize;

        //Name the drawing winner
        DrawingReceipt storage drawingReceipt = DrawingReceipts[winningReceiptID];
        drawingReceipt.redeemableAmount = winningAmount;
        emit RaffleWinner(context.drawingID, context.cohort, winningReceiptID, winnerIndex+1, winningAmount); 
    }

    function redeemDrawingReceipts(uint256[] calldata receiptIDs) external {
        uint256 redeemableAmount; 
        address erc20Token = Drawings[DrawingReceipts[receiptIDs[0]].drawingID].erc20Token; //erc20 address of the first token
        for(uint256 i =0; i < receiptIDs.length; i++) {
            DrawingReceipt memory drawingReceipt = DrawingReceipts[receiptIDs[i]];
            Drawing storage drawing = Drawings[drawingReceipt.drawingID];
            require(erc20Token == drawing.erc20Token, "Invalid"); 
            if(drawing.mintsNFTs) {
                require((receiptManager.ownerOf(receiptIDs[i]) == msg.sender), "Not owned");
            }
            else {
                require((unmintedReceiptsToOwners[receiptIDs[i]] == msg.sender), "Not owned");
            }            
            if(drawingReceipt.redeemableAmount >0) {
                redeemableAmount += drawingReceipt.redeemableAmount;
                DrawingReceipts[receiptIDs[i]].redeemableAmount = 0; 
                emit DrawingReceiptRedeemed(receiptIDs[i], drawingReceipt.drawingID, drawingReceipt.redeemableAmount); 
            }
        }
        if(redeemableAmount !=0) {
            IERC20(erc20Token).transfer(
                msg.sender,
                redeemableAmount
            );
        }
    }


    function cancelDrawing(uint256 drawingID) external {
        Drawing storage drawing = Drawings[drawingID];
        if (!promotionsManager.isApprovedOperator(msg.sender, drawing.owner)) {
            revert NotApproved(); 
        }
        uint256 numParticipants = promotionIDToReceiptIDs[drawingID].length; 
        if (drawing.endTime > block.timestamp && numParticipants < drawing.maxSlots) {
            uint256 numBuyersToCompensate = numParticipants % drawing.cohortSize;
            // Ensure we are compensating the last `numBuyersToCompensate` participants
            uint256 startIndex = numParticipants - numBuyersToCompensate; // Start index for the last `numBuyersToCompensate`
            uint256 endIndex = numParticipants - 1; // End index for the last participant

            // Create a new array to hold the slice
            uint256[] memory receiptsToCompensate = new uint256[](numBuyersToCompensate);

            // Copy the elements from the original array to the new array
            for (uint256 i = startIndex; i <= endIndex; i++) {
                receiptsToCompensate[i - startIndex] = promotionIDToReceiptIDs[drawingID][i];
            }

            // Pass the array to the rebate function
            rebateDrawingReceipts(receiptsToCompensate, drawing.rebateAmount);
            emit DrawingCancelled(drawingID, numBuyersToCompensate);
            drawing.maxSlots = numParticipants; 
            drawing.returnedCustody = true; 
        }
    }

    function rebateDrawingReceipts(
        uint256[] memory tokenIds,
        uint256 rebateAmount
    ) internal {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            DrawingReceipt storage drawingReceipt = DrawingReceipts[
                tokenIds[i]
            ];
            drawingReceipt.redeemableAmount = rebateAmount;
        }
    }

    function retrieveExcessDrawingCustody(uint256 drawingID) public {
        Drawing memory drawing = Drawings[drawingID];
        if (!promotionsManager.isApprovedOperator(msg.sender, drawing.owner)) {
            revert NotApproved(); 
        }
        uint256 numParticipants = promotionIDToReceiptIDs[drawingID].length;
        // Check if the custody has already been returned
        require(block.timestamp > drawing.endTime || drawing.maxSlots == numParticipants, "Ineligible");
        require(!drawing.returnedCustody, "Already Returned");

        // Calculate excess custody based on remaining participants in last cohort
        uint256 excessCustody = (numParticipants % drawing.cohortSize) * drawing.rebateAmount;

        // Calculate and deduct commission
        uint256 commissionAmount = (excessCustody) * commission / 10000;
        earnedCommissions[drawing.erc20Token] += commissionAmount;

        // Pay the owner the remaining amount after deducting the commission
        IERC20(drawing.erc20Token).transfer(promotionsManager.getReceiverAddress(drawing.owner, drawing.erc20Token), excessCustody - commissionAmount); 

        // Update storage values
        Drawing storage drawingStorage = Drawings[drawingID];
        drawingStorage.returnedCustody = true;

        //Emit the event
        emit DrawingCustodyRedeemed(drawingID, excessCustody); 
    }


    function setPromotionURI(uint256 promotionID, string calldata newURIRoot) external override {
        Drawing storage drawing =Drawings[promotionID]; 
        if (!promotionsManager.isApprovedOperator(msg.sender, drawing.owner)) {
            revert NotApproved(); 
        }
        if (!drawing.mintsNFTs) {
            revert NotCustomURI(); 
        }
        if(bytes(receiptManager.customURIRoot(promotionID)).length != 0) {
            revert URIAlreadSet();
        }
        receiptManager.modifyPromotionURI(promotionID, newURIRoot);
    }

    function setRoyalty(uint256 promotionID, uint256 basisPoints) external override {
        Drawing storage drawing = Drawings[promotionID]; 
        if (!promotionsManager.isApprovedOperator(msg.sender, drawing.owner)) {
            revert NotApproved(); 
        }
        receiptManager.setRoyalty(promotionID, basisPoints);
    }

}

contracts/PromotionManagerv9.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

error NotCustomURI();
error URIAlreadSet();

import "../interfaces/IReceiptManager.sol";
import "../interfaces/IPromotionsManager.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

abstract contract PromotionManager {
    uint256 public commission = 200; // basis points (divided by 10,000)
    address payable public bank;
    address public owner;
    address public receiptManagerAddress;
    address public promotionsManagerAddress;
    mapping(address => uint256) public earnedCommissions;
    mapping(address => uint256) public withdrawnCommissions;
    mapping(address => mapping(address => bool)) public approvedOperators;
    mapping(address => address[]) public arrayOfApprovedOperators;
    mapping(address => uint256[]) public addressToPromotions;
    mapping(uint256 => address) public unmintedReceiptsToOwners;
    mapping(uint256 => uint256[]) public promotionIDToReceiptIDs;
    mapping(uint256 => uint256) public receiptIDToPromotionID;
    IReceiptManager public receiptManager;
    IPromotionsManager public promotionsManager;

    modifier onlyOwner() {
        require(msg.sender == owner, "NotOwner");
        _;
    }

    modifier onlyReceiptManager() {
        require(msg.sender == receiptManagerAddress, "NotOwner");
        _;
    }

    modifier onlyApprovedOperators(address parentAccount) {
        require(
            promotionsManager.isApprovedOperator(msg.sender, parentAccount),
            "Not Approved"
        );
        _;
    }

    // function getReceiptInfo(uint256 receiptID) public view virtual returns(uint256 promotionID, uint256 participantNumber);
    function setPromotionURI(
        uint256 promotionID,
        string calldata newURIRoot
    ) external virtual;

    function setRoyalty(
        uint256 promotionID,
        uint256 royaltyBPs
    ) external virtual;

    function getPromotionReceipts(
        uint256 promotionID
    ) public view returns (uint256[] memory) {
        uint256[] memory receipts = promotionIDToReceiptIDs[promotionID];
        return (receipts);
    }

    function getNumberOfParticipants(
        uint256 promotionID
    ) public view returns (uint256) {
        return promotionIDToReceiptIDs[promotionID].length;
    }

    function getPromotionsByOwner(
        address promotionOwner
    ) public view returns (uint256[] memory) {
        return addressToPromotions[promotionOwner];
    }

    function setBank(address payable newBank) external onlyOwner {
        bank = payable(newBank);
    }

    function setOwner(address payable newOwner) external onlyOwner {
        owner = payable(newOwner);
    }

    function setReceiptManager(
        address _receiptManagerAddress
    ) external onlyOwner {
        receiptManagerAddress = _receiptManagerAddress;
        receiptManager = IReceiptManager(_receiptManagerAddress);
    }

    function setPromotionsManager(
        address _promotionsManagerAddress
    ) external onlyOwner {
        promotionsManagerAddress = _promotionsManagerAddress;
        promotionsManager = IPromotionsManager(_promotionsManagerAddress);
    }

    function withdrawCommissions(address erc20Token) external onlyOwner {
        IERC20(erc20Token).transfer(
            bank,
            earnedCommissions[erc20Token] - withdrawnCommissions[erc20Token]
        );
        withdrawnCommissions[erc20Token] = earnedCommissions[erc20Token];
    }

    function setCommission(uint256 newCommissionBPs) external onlyOwner {
        require(newCommissionBPs <= 10000, "Inv");
        commission = newCommissionBPs;
    }
}

contracts/PromotionsManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

event OperatorApproved(address indexed parentAccount, address indexed approvedOperator);
event OperatorRemoved(address indexed parentAccount, address indexed removedOperator);
event AdministratorApproved(address indexed parentAccount, address indexed approvedAdministrator);
event AdministratorRemoved(address indexed parentAccount, address indexed removedAdministrator);
error NotCustomURI();
error URIAlreadSet();


contract PromotionsManager {
    mapping(address => mapping(address => bool)) public  approvedOperators;
    mapping(address => address[]) public  arrayOfApprovedOperators;
    mapping(address => mapping(address => bool)) public  approvedAdministrators;
    mapping(address => address[]) public  arrayOfApprovedAdministrators;
    mapping(address => mapping(address => address)) public receiverAccounts;   

    function approveOperator(address approvedOperator, address parentAccount) public {
        require(approvedOperator != address(0), "Cannot approve zero address.");
        require(
            !approvedOperators[parentAccount][approvedOperator],
            "Operator already approved."
        );
        require(parentAccount ==  msg.sender || approvedAdministrators[parentAccount][msg.sender], "Not approved administrator");

        approvedOperators[parentAccount][approvedOperator] = true;
        arrayOfApprovedOperators[parentAccount].push(approvedOperator);

        emit OperatorApproved(parentAccount, approvedOperator);
    }

    function removeOperator(address toRemove, address parentAccount) public {
        require(
            approvedOperators[parentAccount][toRemove],
            "Operator not approved."
        );
        require(parentAccount ==  msg.sender || approvedAdministrators[parentAccount][msg.sender], "Not approved administrator");

        approvedOperators[parentAccount][toRemove] = false;

        // Remove the address from the array
        address[] storage operators = arrayOfApprovedOperators[parentAccount];
        for (uint256 i = 0; i < operators.length; i++) {
            if (operators[i] == toRemove) {
                operators[i] = operators[operators.length - 1]; // Replace with the last element
                operators.pop(); // Remove the last element
                break;
            }
        }

        emit OperatorRemoved(parentAccount, toRemove);
    }

    function approveAdministrator(address approvedAdministrator, bool addOperator) public {
        require(approvedAdministrator != address(0), "Cannot approve zero address.");
        require(
            !approvedAdministrators[msg.sender][approvedAdministrator],
            "Admin already approved."
        );

        approvedAdministrators[msg.sender][approvedAdministrator] = true;
        arrayOfApprovedAdministrators[msg.sender].push(approvedAdministrator);

        emit AdministratorApproved(msg.sender, approvedAdministrator);
        if (addOperator) {
            approveOperator(approvedAdministrator, msg.sender);
        }
    }

    function removeAdministrator(address toRemove, bool removeAsOperator) public {
        require(
            approvedAdministrators[msg.sender][toRemove],
            "Administrator not approved."
        );
        approvedAdministrators[msg.sender][toRemove] = false;

        // Remove the address from the array
        address[] storage operators = arrayOfApprovedAdministrators[msg.sender];
        for (uint256 i = 0; i < operators.length; i++) {
            if (operators[i] == toRemove) {
                operators[i] = operators[operators.length - 1]; // Replace with the last element
                operators.pop(); // Remove the last element
                break;
            }
        }

        emit AdministratorRemoved(msg.sender, toRemove);
        if (removeAsOperator && approvedOperators[msg.sender][toRemove]) {
            removeOperator(toRemove, msg.sender);
        }
    }

    function isApprovedOperator(address operator, address parentAccount) public view returns(bool) {
        return (approvedOperators[parentAccount][operator] || operator == parentAccount); 
    }

    function getApprovedOperators(
        address masterAccount
    ) external view returns (address[] memory) {
        return arrayOfApprovedOperators[masterAccount];
    }

    function getApprovedAdministrators(
        address masterAccount
    ) external view returns (address[] memory) {
        return arrayOfApprovedAdministrators[masterAccount];
    }

    function updateReceiverAddress(address tokenAddress, address receiverAddress) public {
        receiverAccounts[msg.sender][tokenAddress] = receiverAddress; 
    }

    function getReceiverAddress(address userAddress, address tokenAddress) public view returns (address) {
        address receiver = receiverAccounts[userAddress][tokenAddress];
        if (receiver == address(0)) {
            return userAddress;
        } else {
            return receiver;
        }
    }
}

